-- ----------------------------
-- Table structure for `post_comment`
-- ----------------------------
DROP TABLE IF EXISTS `post_comment`;
CREATE TABLE `post_comment` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `parent` int(11) unsigned DEFAULT NULL COMMENT '父级评论',
  `post_id` int(11) unsigned NOT NULL COMMENT '文章ID',
  `comment` text CHARACTER SET utf8mb4,
  `status` tinyint(1) NOT NULL DEFAULT '1' COMMENT '1为正常 0为禁用',
  `user_id` int(11) unsigned NOT NULL COMMENT '用户ID',
  `like_count` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '喜欢数',
  `ip` varchar(255) NOT NULL COMMENT '评论者ip地址',
  `created_at` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '创建时间',
  `updated_at` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '修改时间',
  PRIMARY KEY (`id`),
  KEY `post_id` (`post_id`),
  KEY `user_id` (`user_id`)
) ENGINE=MyISAM AUTO_INCREMENT=151 DEFAULT CHARSET=utf8;

